sys_script_include.script:
var DriverUtils = Class.create();
DriverUtils.prototype = {
    initialize: function() {},

    isJuniorRookie: function(driverInfo) {
        if (driverInfo.helmet.nil()) {
            return true;
        } else {
            return false;
        }
    },

    getRandomAge: function(isJunior) {
        var ageInterval;
        var minAge;
        if (isJunior) {
            ageInterval = gs.getProperty('x_704397_generally.juniorRookieAgeInterval');
            minAge = gs.getProperty('x_704397_generally.juniorRookieMinAge');
        } else {
            ageInterval = gs.getProperty('x_704397_generally.rookieAgeInterval');
            minAge = gs.getProperty('x_704397_generally.rookieMinAge');
        }
        return Math.floor(Math.random() * ageInterval + parseInt(minAge));
    },

    getRandomSkill: function(isJunior) {
        var skillInterval;
        if (isJunior) {
            skillInterval = gs.getProperty('x_704397_generally.juniorRookieSkillInterval');
        } else {
            skillInterval = gs.getProperty('x_704397_generally.rookieSkillInterval');
        }
        return Math.floor(Math.random() * skillInterval);
    },

    generateRookie: function(driverInfo) {
        var rookie = new GlideRecord('x_704397_generally_driver');
        rookie.initialize();
        rookie.setValue('abbrev', driverInfo.getValue('sys_id'));
        rookie.setValue('display', driverInfo.getValue('abbrev'));
        rookie.setValue('age', this.getRandomAge(this.isJuniorRookie(driverInfo)));
        rookie.setValue('skill', this.getRandomSkill(this.isJuniorRookie(driverInfo)));
        var teamUtils = new TeamUtils();
        rookie.setValue('team', teamUtils.getEmploymentAgency());
        rookie.insert();
    },

    signContract: function(driver, team) {
        var signatory = new GlideRecord('x_704397_generally_driver');
        signatory.addQuery('sys_id', driver);
        signatory.query();

        if (signatory.next()) {
            signatory.setValue('display', signatory.getDisplayValue('abbrev') + " (" +
                team.getElement('name.abbrev') + ")");
            signatory.setValue('team', team.getValue('sys_id'));
            if (team.getValue('junior_driver') === driver) {
                signatory.setValue('junior', 'true');
            } else {
                signatory.setValue('junior', 'false');
            }
            signatory.update();
        }
    },

    getPositionCount: function(driver, position) {
        var result = new GlideRecord('x_704397_generally_result');
        result.addQuery('year', gs.getProperty('x_704397_generally.actualYear'));
        result.addQuery('driver', driver.getValue('sys_id'));
        result.addQuery('position', position);
        result.query();
        return result.getRowCount();
    },

    calculatePoints: function(driver, pointAllocation) {
        var points = 0;
        for (var i = 0; i < gs.getProperty('x_704397_generally.lastPosition'); i++) {
            points = points + this.getPositionCount(driver, i + 1) * parseInt(pointAllocation[i]);
        }
        return points;
    },

    updatePoints: function() {
        var driver = new GlideRecord('x_704397_generally_driver');
        var teamUtils = new TeamUtils();
        driver.addEncodedQuery(
            'team.year=' + gs.getProperty('x_704397_generally.actualYear') +
            '^team!=' + teamUtils.getEmploymentAgency()
        );
        driver.query();
        var resultUtils = new ResultUtils();
        while (driver.next()) {
            var series;
            if (driver.getValue('junior') === 'true') {
                series = gs.getProperty('x_704397_generally.juniorSeries');
            } else {
                series = driver.getElement('team.series');
            }
            driver.setValue(
                'points',
                this.calculatePoints(driver, resultUtils.getPointAllocation(series))
            );
            driver.update();
        }
    },

    getFinishes: function(driver) {
        var finishes = "";
        for (var i = 0; i < gs.getProperty('x_704397_generally.lastPosition'); i++) {
            if (i == gs.getProperty('x_704397_generally.lastPosition') - 1) {
                finishes += this.getPositionCount(driver, i + 1)
                    .toString(gs.getProperty('x_704397_generally.radix'));
            } else {
                finishes += this.getPositionCount(driver, i + 1)
                    .toString(gs.getProperty('x_704397_generally.radix')) + ",";
            }
        }
        return finishes;
    },

    getReversedPointsProgress: function(driver, series) {
        var resultUtils = new ResultUtils();
        var pointAllocation = resultUtils.getPointAllocation(series);
        var progress = "";
        for (var i = resultUtils.getRoundCount(series); i > 0; i--) {
            var result = new GlideRecord('x_704397_generally_result');
            result.addQuery('year', gs.getProperty('x_704397_generally.actualYear'));
            result.addQuery('driver', driver.getValue('sys_id'));
            result.addQuery('round', i);
            result.query();
            if (result.next()) {
                var position = result.getValue('position');
                if (i == gs.getProperty('x_704397_generally.firstPosition')) {
                    progress += pointAllocation[position - 1]
                        .toString(gs.getProperty('x_704397_generally.radix'));
                } else {
                    progress += pointAllocation[position - 1]
                        .toString(gs.getProperty('x_704397_generally.radix')) + ",";
                }
            }
        }
        return progress;
    },

    getFinalRacePosition: function(driver, series) {
        var result = new GlideRecord('x_704397_generally_result');
        result.addQuery('driver', driver.getValue('sys_id'));
        var resultUtils = new ResultUtils();
        result.addQuery('round', resultUtils.getRoundCount(series));
        result.query();
        if (result.next()) {
            return result.getValue('position');
        }
    },

    getFinalRacePoints: function(driver, series) {
        var resultUtils = new ResultUtils();
        var pointAllocation = resultUtils.getPointAllocation(series);
        return parseInt(pointAllocation[this.getFinalRacePosition(driver, series) - 1]);
    },

    replaceFinishes: function(finishes, position, positionCount) {
        return finishes.substring(0, position * 2 - 2) +
            positionCount.toString(gs.getProperty('x_704397_generally.radix')) +
            finishes.substring(position * 2 - 1);
    },

    getPerformance: function(isFinal, driver, series) {
        var points = parseInt(driver.getValue('points'));
        var finishes = this.getFinishes(driver);
        var progress = this.getReversedPointsProgress(driver, series);
        if (!isFinal) {
            points -= this.getFinalRacePoints(driver, series);
            var position = this.getFinalRacePosition(driver, series);
            var positionCount = parseInt(finishes.charAt(position * 2 - 2),
                gs.getProperty('x_704397_generally.radix'));
            finishes = this.replaceFinishes(finishes, position, positionCount - 1);
            progress = progress.substring(2, progress.length);
        }
        var performance = {
            driver: driver.getValue('sys_id'),
            points: points,
            finishes: finishes,
            reversedPointsProgress: progress
        };
        return performance;
    },

    calculateStandings: function(isFinal, series) {
        var driver = new GlideRecord('x_704397_generally_driver');
        driver.addQuery('team.year', gs.getProperty('x_704397_generally.actualYear'));
        if (series === gs.getProperty('x_704397_generally.juniorSeries')) {
            driver.addQuery('junior', 'true');
        } else {
            driver.addQuery('team.series', series);
            driver.addQuery('junior', 'false');
        }
        driver.query();
        var standings = [];
        while (driver.next()) {
            standings.push(this.getPerformance(isFinal, driver, series));
        }
        var resultUtils = new ResultUtils();
        resultUtils.sortStandings(standings);
        return standings;
    },

    updateStandings: function(standings) {
        for (var i = 0; i < gs.getProperty('x_704397_generally.lastPosition'); i++) {
            var driver = new GlideRecord('x_704397_generally_driver');
            driver.addQuery('sys_id', standings[i].driver);
            driver.query();
            if (driver.next()) {
                driver.setValue('standing', i + 1);
                driver.update();
            }
        }
    },

    calculateContenderHappyStandings: function(series, standings) {
        var resultUtils = new ResultUtils();
        var pointAllocation = resultUtils.getPointAllocation(series);
        standings.reverse();
        for (var i = 0; i < gs.getProperty('x_704397_generally.lastPosition'); i++) {
            var points = parseInt(pointAllocation[i]);
            var position = i + 1;
            var positionCount = parseInt(standings[i].finishes.charAt(position * 2 - 2),
                gs.getProperty('x_704397_generally.radix'));
            var finishes = this.replaceFinishes(
                standings[i].finishes, position, positionCount + 1);
            var progress = standings[i].reversedPointsProgress.replace(/^/,
                points.toString(gs.getProperty('x_704397_generally.radix')) + ",");
            standings[i].points += points;
            standings[i].finishes = finishes;
            standings[i].reversedPointsProgress = progress;
        }
    },

    getContenderHappyPerformance: function(series, performance) {
        var resultUtils = new ResultUtils();
        var pointAllocation = resultUtils.getPointAllocation(series);
        var firstPosition = parseInt(gs.getProperty('x_704397_generally.firstPosition'));
        var points = parseInt(pointAllocation[firstPosition - 1]);
        var positionCount = parseInt(performance.finishes.charAt(firstPosition * 2 - 2),
            gs.getProperty('x_704397_generally.radix'));
        var finishes = this.replaceFinishes(
            performance.finishes, firstPosition, positionCount + 1);
        var progress = performance.reversedPointsProgress.replace(/^/,
            points.toString(gs.getProperty('x_704397_generally.radix')) + ",");
        performance.points += points;
        performance.finishes = finishes;
        performance.reversedPointsProgress = progress;
        return performance;
    },

    isContender: function(series, performance, standings) {
        var happyStandings = standings.slice();
        happyStandings.push(this.getContenderHappyPerformance(series, performance));
        var resultUtils = new ResultUtils();
        resultUtils.sortStandings(happyStandings);
        var firstPosition = parseInt(gs.getProperty('x_704397_generally.firstPosition'));
        if (happyStandings[firstPosition - 1].driver === performance.driver) {
            return true;
        }
        return false;
    },

    updateContenders: function(series, standings) {
        this.calculateContenderHappyStandings(series, standings);
        for (var i = 0; i < gs.getProperty('x_704397_generally.lastPosition'); i++) {
            if (this.isContender(series, standings[i], standings)) {
                var driver = new GlideRecord('x_704397_generally_driver');
                driver.addQuery('sys_id', standings[i].driver);
                driver.query();
                if (driver.next()) {
                    driver.setValue('contender', 'true');
                    driver.update();
                }
            }
        }
    },

    updateSeasonInfo: function() {
        this.updatePoints();
        var series = gs.getProperty('x_704397_generally.series').split(',');
        for (var i = 0; i < series.length; i++) {
            this.updateStandings(this.calculateStandings(true, series[i]));
            this.updateContenders(series[i], this.calculateStandings(false, series[i]));
        }
    },

    type: 'DriverUtils'
};
