sys_script_include.script:
var TeamUtils = Class.create();
TeamUtils.prototype = {
    initialize: function() {},

    getEmploymentAgency: function() {
        var team = new GlideRecord('x_704397_generally_team');
        team.addQuery('year', gs.getProperty('x_704397_generally.actualYear'));
        team.addNullQuery('name');
        team.query();
        if (team.next()) {
            return team.getValue('sys_id');
        }
    },

    getRandomResource: function() {
        return Math.floor(Math.random() * gs.getProperty('x_704397_generally.resourceInterval'));
    },

    generateNewTeam: function(teamInfo) {
        var newTeam = new GlideRecord('x_704397_generally_team');
        newTeam.initialize();
        newTeam.setValue('year', gs.getProperty('x_704397_generally.actualYear'));
        newTeam.setValue('name', teamInfo.getValue('sys_id'));
        newTeam.setValue('series', 'wrc');
        newTeam.setValue('prestige', 0);
        newTeam.setValue('pressure', 0);
        newTeam.setValue('resource', this.getRandomResource());
        newTeam.insert();
    },

    confirmLineup: function(team, driver1, driver2, juniorDriver) {
        var driverUtils = new DriverUtils();
        driverUtils.signContract(driver1, team);
        driverUtils.signContract(driver2, team);
        driverUtils.signContract(juniorDriver, team);
    },

    updatePoints: function() {
        var team = new GlideRecord('x_704397_generally_team');
        team.addEncodedQuery(
            'year=' + gs.getProperty('x_704397_generally.actualYear') +
            '^sys_id!=' + this.getEmploymentAgency()
        );
        team.query();
        while (team.next()) {
            var points = team.getElement('driver1.points') + team.getElement('driver2.points');
            team.setValue('points', points);
            team.update();
        }
    },

    getPositionCount: function(team, position) {
        var result = new GlideRecord('x_704397_generally_result');
        result.addQuery('driver', team.getValue('driver1'))
            .addOrCondition('driver', team.getValue('driver2'));
        result.addQuery('position', position);
        result.query();
        return result.getRowCount();
    },

    getFinishes: function(team) {
        var finishes = "";
        for (var i = 0; i < gs.getProperty('x_704397_generally.lastPosition'); i++) {
            var positionCount = this.getPositionCount(team, i + 1)
                .toString(gs.getProperty('x_704397_generally.radix'));
            if (i == gs.getProperty('x_704397_generally.lastPosition') - 1) {
                finishes += positionCount;
            } else {
                finishes += positionCount + ",";
            }
        }
        return finishes;
    },

    getReversedPointsProgress: function(team, series) {
        var resultUtils = new ResultUtils();
        var pointAllocation = resultUtils.getPointAllocation(series);
        var progress = "";
        for (var i = resultUtils.getRoundCount(series); i > 0; i--) {
            var result = new GlideRecord('x_704397_generally_result');
            result.addQuery('driver', team.getValue('driver1'))
                .addOrCondition('driver', team.getValue('driver2'));
            result.addQuery('round', i);
            result.query();
            var points = 0;
            while (result.next()) {
                var position = result.getValue('position');
                points += parseInt(pointAllocation[position - 1]);
            }
            points = points.toString(gs.getProperty('x_704397_generally.radix'));
            if (i == gs.getProperty('x_704397_generally.firstPosition')) {
                progress += points;
            } else {
                progress += points + ",";
            }
        }
        return progress;
    },

    getPerformance: function(isFinal, team, series) {
        var points = parseInt(team.getValue('points'));
        var finishes = this.getFinishes(team);
        var progress = this.getReversedPointsProgress(team, series);
        if (!isFinal) {
            var firstPosition = gs.getProperty('x_704397_generally.firstPosition');
            var seats = gs.getProperty('x_704397_generally.seatsPerTeam');
            var driverUtils = new DriverUtils();
            for (i = firstPosition; i <= seats; i++) {
                var driver = new GlideRecord('x_704397_generally_driver');
                driver.get(team.getValue('driver' + i));
                points -= driverUtils.getFinalRacePoints(driver, series);
                var position = driverUtils.getFinalRacePosition(driver, series);
                var positionCount = parseInt(finishes.charAt(position * 2 - 2),
                    gs.getProperty('x_704397_generally.radix'));
                finishes = driverUtils.replaceFinishes(finishes, position, positionCount - 1);
            }
            progress = progress.substring(2, progress.length);
        }
        var performance = {
            team: team.getValue('sys_id'),
            points: points,
            finishes: finishes,
            reversedPointsProgress: progress
        };
        return performance;
    },

    calculateStandings: function(isFinal, series) {
        var team = new GlideRecord('x_704397_generally_team');
        team.addQuery('year', gs.getProperty('x_704397_generally.actualYear'));
        team.addQuery('series', series);
        team.query();
        var standings = [];
        while (team.next()) {
            standings.push(this.getPerformance(isFinal, team, series));
        }
        var resultUtils = new ResultUtils();
        resultUtils.sortStandings(standings);
        return standings;
    },

    updateStandings: function(standings) {
        var lastPosition = gs.getProperty('x_704397_generally.lastPosition') /
            gs.getProperty('x_704397_generally.seatsPerTeam');
        for (var i = 0; i < lastPosition; i++) {
            var team = new GlideRecord('x_704397_generally_team');
            team.addQuery('sys_id', standings[i].team);
            team.query();
            if (team.next()) {
                team.setValue('standing', i + 1);
                team.update();
            }
        }
    },

    calculateContenderHappyStandings: function(series, standings) {
        var happyStandings = JSON.parse(JSON.stringify(standings));
        happyStandings.reverse();
        var seats = gs.getProperty('x_704397_generally.seatsPerTeam');
        var lastPosition = gs.getProperty('x_704397_generally.lastPosition') / seats;
        var firstPosition = parseInt(gs.getProperty('x_704397_generally.firstPosition'));
        var resultUtils = new ResultUtils();
        var pointAllocation = resultUtils.getPointAllocation(series);
        var driverUtils = new DriverUtils();
        for (var i = 0; i < lastPosition; i++) {
            var points = 0;
            var finishes = happyStandings[i].finishes;
            for (var j = firstPosition; j <= seats; j++) {
                var position = i * seats + j;
                points += parseInt(pointAllocation[position - 1]);
                var positionCount = parseInt(happyStandings[i].finishes.charAt(position * 2 - 2),
                    gs.getProperty('x_704397_generally.radix'));
                finishes = driverUtils.replaceFinishes(finishes, position, positionCount + 1);
            }
            var progress = happyStandings[i].reversedPointsProgress.replace(/^/,
                points.toString(gs.getProperty('x_704397_generally.radix')) + ",");
            happyStandings[i].points += points;
            happyStandings[i].finishes = finishes;
            happyStandings[i].reversedPointsProgress = progress;
        }
        return happyStandings;
    },

    getContenderHappyPerformance: function(series, performance) {
        var resultUtils = new ResultUtils();
        var pointAllocation = resultUtils.getPointAllocation(series);
        var firstPosition = parseInt(gs.getProperty('x_704397_generally.firstPosition'));
        var seats = parseInt(gs.getProperty('x_704397_generally.seatsPerTeam'));
        var points = 0;
        var finishes = performance.finishes;
        var driverUtils = new DriverUtils();
        for (i = firstPosition; i <= seats; i++) {
            points += parseInt(pointAllocation[i - 1]);
            var positionCount = parseInt(performance.finishes.charAt(i * 2 - 2),
                gs.getProperty('x_704397_generally.radix'));
            finishes = driverUtils.replaceFinishes(finishes, i, positionCount + 1);
        }
        var progress = performance.reversedPointsProgress.replace(/^/,
            points.toString(gs.getProperty('x_704397_generally.radix')) + ",");
        performance.points += points;
        performance.finishes = finishes;
        performance.reversedPointsProgress = progress;
        return performance;
    },

    isContender: function(series, performance, standings) {
        var happyStandings = standings.slice();
        happyStandings.push(this.getContenderHappyPerformance(series, performance));
        var resultUtils = new ResultUtils();
        resultUtils.sortStandings(happyStandings);
        var firstPosition = parseInt(gs.getProperty('x_704397_generally.firstPosition'));
        if (happyStandings[firstPosition - 1].team === performance.team) {
            return true;
        }
        return false;
    },

    updateContenders: function(series, standings) {
        var happyStandings = this.calculateContenderHappyStandings(series, standings);
        var seats = gs.getProperty('x_704397_generally.seatsPerTeam');
        var lastPosition = gs.getProperty('x_704397_generally.lastPosition') / seats;
        for (var i = 0; i < lastPosition; i++) {
            if (this.isContender(series, standings[i], happyStandings)) {
                var team = new GlideRecord('x_704397_generally_team');
                team.addQuery('sys_id', standings[i].team);
                team.query();
                if (team.next()) {
                    team.setValue('contender', 'true');
                    team.update();
                }
            }
        }
    },

    updateSeasonInfo: function() {
        this.updatePoints();
        var series = gs.getProperty('x_704397_generally.series').split(',').filter(function(s) {
            return s !== gs.getProperty('x_704397_generally.juniorSeries');
        });
        for (var i = 0; i < series.length; i++) {
            this.updateStandings(this.calculateStandings(true, series[i]));
            this.updateContenders(series[i], this.calculateStandings(false, series[i]));
        }
    },

    type: 'TeamUtils'
};
