sys_script_include.script:
var TeamUtils = Class.create();
TeamUtils.prototype = {
    initialize: function() {},

    getEmploymentAgency: function() {
        var team = new GlideRecord('x_704397_generally_team');
        var actualYear = gs.getProperty('x_704397_generally.actualYear');
        team.addQuery('year', actualYear);
        team.addNullQuery('name');
        team.query();
        if (team.next()) {
            return team.getValue('sys_id');
        }
    },

    type: 'TeamUtils'
};
