sys_script_include.script:
var TeamUtils = Class.create();
var driverUtils = new DriverUtils();

TeamUtils.prototype = {
    initialize: function() {},
    type: 'TeamUtils',

    currentYear: parseInt(gs.getProperty('x_704397_generally.currentYear')),
    resourceInterval: parseInt(gs.getProperty('x_704397_generally.resourceInterval')),

    employmentAgency: 'year=' + gs.getProperty('x_704397_generally.currentYear') + '^nameISEMPTY',

    getEmploymentAgency: function() {
        var team = new GlideRecord('x_704397_generally_team');
        team.addEncodedQuery(this.employmentAgency);
        team.query();
        if (team.next()) {
            return team.getUniqueValue();
        }
    },

    getRandomResource: function() {
        return Math.floor(Math.random() * this.resourceInterval);
    },

    generateNewTeam: function(teamInfo) {
        var newTeam = new GlideRecord('x_704397_generally_team');
        newTeam.initialize();
        newTeam.setValue('year', this.currentYear);
        newTeam.setValue('name', teamInfo.getUniqueValue());
        newTeam.setValue('series', 'wrc');
        newTeam.setValue('prestige', 0);
        newTeam.setValue('pressure', 0);
        newTeam.setValue('resource', this.getRandomResource());
        newTeam.insert();
    },

    confirmLineup: function(team, driver1, driver2, juniorDriver) {
        driverUtils.signContract(driver1, team);
        driverUtils.signContract(driver2, team);
        driverUtils.signContract(juniorDriver, team);
    }
};
