sys_script_include.script:
var ResultUtils = Class.create();
ResultUtils.prototype = Object.extendsObject(Utils, {
    initialize: function() {
        gs.include('Utils');
    },
    type: 'ResultUtils',

    incompleteRounds: function() {
        return 'year=' + this.currentYear + '^driverISEMPTY';
    },

    finishesAtPosition: function(driverId, position) {
        return 'driver=' + driverId + '^position=' + position;
    },

    currentCalendar: function(series) {
        return 'year=' + this.currentYear + '^series=' + series;
    },

    resultAtRound: function(driverId, round) {
        return 'year=' + this.currentYear + '^driver=' + driverId + '^round=' + round;
    },

    isSeasonCompleted: function() {
        var result = new GlideRecord('x_704397_generally_result');
        result.addEncodedQuery(this.incompleteRounds());
        result.query();
        if (result.next()) {
            return false;
        }
        return true;
    },

    getPositionCount: function(driverId, position) {
        var result = new GlideRecord('x_704397_generally_result');
        result.addEncodedQuery(this.finishesAtPosition(driverId, position));
        result.query();
        return result.getRowCount();
    },

    getRoundCount: function(series) {
        var result = new GlideAggregate('x_704397_generally_result');
        result.setGroup(false);
        result.addEncodedQuery(this.currentCalendar(series));
        result.addAggregate('MAX', 'round');
        result.query();
        if (result.next()) {
            return result.getAggregate('MAX', 'round');
        }
    },

    getFinishes: function(driverId) {
        var finishes = "";
        for (var i = 1; i < this.lastPosition; i++) {
            finishes += this.getPositionCount(driverId, i).toString(this.radix) + ",";
        }
        finishes += this.getPositionCount(driverId, this.lastPosition).toString(this.radix);
        return finishes;
    },

    getReversedPointsProgress: function(driverId, series) {
        var progress = "";
        for (var i = this.getRoundCount(series); i > 0; i--) {
            var result = new GlideRecord('x_704397_generally_result');
            result.addEncodedQuery(this.resultAtRound(driverId, i));
            result.query();
            if (result.next()) {
                var position = result.getValue('position');
                if (i > 1) {
                    progress += this.getPointAllocation(series)[position - 1]
                        .toString(this.radix) + ",";
                } else {
                    progress += this.getPointAllocation(series)[position - 1]
                        .toString(this.radix);
                }
            }
        }
        return progress;
    }
});
