sys_script_include.script:
var ResultUtils = Class.create();
ResultUtils.prototype = Object.extendsObject(Utils, {
    initialize: function() {
        gs.include('Utils');
    },
    type: 'ResultUtils',

    incompleteRounds: function() {
        return 'year=' + this.currentYear + '^driverISEMPTY';
    },

    positionCount: function(driverId, position) {
        return '^driver=' + driverId + '^position=' + position;
    },

    isSeasonCompleted: function() {
        var result = new GlideRecord('x_704397_generally_result');
        result.addEncodedQuery(this.incompleteRounds());
        result.query();
        if (result.next()) {
            return false;
        }
        return true;
    },

    getPositionCount: function(driverId, position) {
        var result = new GlideRecord('x_704397_generally_result');
        result.addEncodedQuery(this.positionCount(driverId, position));
        result.query();
        return result.getRowCount();
    }
});
